MER_SCHEDULE C ON SIM_I_CODE=C.CS_I_CODE inner join PRODUCT_MASTER PROD on S.SIM_I_CODE=PROD.PROD_I_CODE WHERE S.ES_DELETE=0 AND I.ES_DELETE=0 AND G.ES_DELETE=0 AND PROD.ES_DELETE=0 AND S.SIM_COMP_ID='" + Convert.ToInt32(Session["CompanyId"]) + "' and datepart(mm,CS_DATE)='" + dtMonth.ToString("MM") + "' and datepart(yyyy,CS_DATE)='" + dtMonth.ToString("yyyy") + "' and PROD.PROD_RAW_TYPE='AC4B' ) as Temp1 select * into #Temp2 from(SELECT DISTINCT  PROD.PROD_RAW_TYPE,ISNULL(PROD_CORE_WT,0) as PROD_CORE_WT,ISNULL(PROD_CAST_WT,0) AS PROD_CAST_WT,ROUND(isnull((isnull((select SUM(ISNULL(CS_SCHEDULE_QTY,0)) FROM CUSTOMER_SCHEDULE where CUSTOMER_SCHEDULE.CS_I_CODE=I.I_CODE and CUSTOMER_SCHEDULE.ES_DELETE=0 and datepart(mm,CS_DATE)='" + dtMonth.ToString("MM") + "' and datepart(yyyy,CS_DATE)='" + dtMonth.ToString("yyyy") + "' ),0) + ISNULL((ISNULL(SIM_RFI_STORE,0)+ ISNULL(SIM_FINISH_GOODS,0)+ ISNULL(SIM_MACHINE_SHOP,0)+ISNULL(SIM_VENDOR,0) +ISNULL(SIM_RFM_STORE,0) +ISNULL(SIM_FOUNDRY,0) +ISNULL(SIM_MAIN_STORE,0)),0) - ISNULL((select SUM(ISNULL(STL_DOC_QTY,0)) AS STL_DOC_QTY from STOCK_LEDGER WHERE STL_I_CODE= I.I_CODE),0)) * isnull((SELECT ISNULL((CAST(RM_CASTING_REJECTION AS FLOAT)/100)+1,0) FROM REJECTION_MASTER WHERE ES_DELETE=0 AND RM_COMP_ID='" + Convert.ToInt32(Session["CompanyId"]) + "' AND RM_DATE=(SELECT MAX(RM_DATE) FROM REJECTION_MASTER WHERE ES_DELETE=0 AND RM_COMP_ID='" + Convert.ToInt32(Session["CompanyId"]) + "')),0),0),0) AS ProdPlan,round(ISNULL((select ISNULL(SRSM_LM25,0) from SAND_RAW_STANDARD_MASTER where SAND_RAW_STANDARD_MASTER.ES_DELETE=0)/1000,0),2) AS Std_Stock,ROUND(ISNULL((SELECT SUM(ISNULL(Turning_IngotLM,0)) AS Turning_IngotLM FROM #TempTurning_IngotLM)/1000,0),2) AS Turning_Ingot,ROUND(ISNULL((SELECT SUM(ISNULL(IngotsLM,0)) AS STL_DOC_QTY FROM #TempIngotsLM)/1000,0),2) AS Ingots,ROUND(ISNULL((SELECT SUM(ISNULL(Runner_RiserLM,0)) AS Runner_RiserLM FROM #TempRunner_RiserLM)/1000,0),2) AS Runner_Riser,0 AS SAND,ROUND(ISNULL((select ISNULL(RM_MELTING_LOSS,0) from REJECTION_MASTER R WHERE RM_COMP_ID='" + Convert.ToInt32(Session["CompanyId"]) + "' AND R.ES_DELETE=0 AND RM_DATE<='" + dtMonth.ToString("dd/MMM/yyyy") + "' ),0),2) AS LossPer FROM GROUP_MASTER G INNER JOIN STANDARD_INVENTARY_MASTER S ON G.GP_CODE =S.SIM_GP_CODE INNER JOIN ITEM_MASTER I ON S.SIM_I_CODE=I.I_CODE INNER JOIN CUSTOMER_SCHEDULE C ON SIM_I_CODE=C.CS_I_CODE inner join PRODUCT_MASTER PROD on S.SIM_I_CODE=PROD.PROD_I_CODE WHERE S.ES_DELETE=0 AND I.ES_DELETE=0 AND G.ES_DELETE=0 AND PROD.ES_DELETE=0 AND S.SIM_COMP_ID='" + Convert.ToInt32(Session["CompanyId"]) + "' and datepart(mm,CS_DATE)='" + dtMonth.ToString("MM") + "' and datepart(yyyy,CS_DATE)='" + dtMonth.ToString("yyyy") + "' and PROD.PROD_RAW_TYPE='LM25' ) as Temp2 select DISTINCT PROD_RAW_TYPE,	PROD_CORE_WT,	PROD_CAST_WT,	case when ProdPlan<0 then 0 else round(isnull(ProdPlan,0)*isnull(PROD_CAST_WT,0)/1000,2) end as ProdPlan,	Std_Stock,	 case when Turning_Ingot<0 then 0 else Turning_Ingot end as Turning_Ingot,case when Ingots<0 then 0 else Ingots end as Ingots,case when Runner_Riser<0 then 0 else Runner_Riser end as Runner_Riser,case when	SAND<0 then 0 else SAND end as SAND,case when LossPer<0 then 0 else LossPer end as LossPer into #Temp3 from #Temp select DISTINCT PROD_RAW_TYPE,	PROD_CORE_WT,	PROD_CAST_WT,	case when ProdPlan<0 then 0 else round(isnull(ProdPlan,0)*isnull(PROD_CAST_WT,0)/1000,2) end as ProdPlan,	Std_Stock,	 case when Turning_Ingot<0 then 0 else Turning_Ingot end as Turning_Ingot,case when Ingots<0 then 0 else Ingots end as Ingots,case when Runner_Riser<0 then 0 else Runner_Riser end as Runner_Riser,case when	SAND<0 then 0 else SAND end as SAND,case when LossPer<0 then 0 else LossPer end as LossPer into #Temp4 from #Temp2   select PROD_RAW_TYPE,round(SUM(ProdPlan),0) as ProdPlan, isnull((select isnull(sum(SRSM_AC4B),0) from #TempSRSM_AC4B),0) AS Std_Stock,case when (round(isnull((select isnull(Turning_Ingot,0) from #TempTurning_Ingot),0)/1000,2))<0 then 0 else  round(isnull((select isnull(Turning_Ingot,0) from #TempTurning_Ingot),0)/1000,2) end AS Turning_Ingot,case when (round(isnull((select isnull(Ingots,0) from #TempIngots),0)/1000,2))<0 then 0 else round(isnull((select isnull(Ingots,0) from #TempIngots),0)/1000,2) end AS Ingots,sum(isnull(SAND,0)) AS SAND,case when (round(isnull((select isnull(Runner_Riser,0) from #TempRunner_Riser),0)/1000,2)) <0 then 0 else round(isnull((select isnull(Runner_Riser,0) from #TempRunner_Riser),0)/1000,2) end AS Runner_Riser,0 as Total,0 as With_Melting_Loss ,0 as Material_Purchase,LossPer from #Temp3 group by PROD_RAW_TYPE,LossPer union select PROD_RAW_TYPE,round(SUM(ProdPlan),0) as ProdPlan,isnull((select isnull(sum(SRSM_LM25),0) from #TempSRSM_LM25),0) AS Std_Stock,case when ( round(isnull((select isnull(sum(Turning_IngotLM),0) from #TempTurning_IngotLM),0)/1000,2)) <0 then 0 else round(isnull((select isnull(sum(Turning_IngotLM),0) from #TempTurning_IngotLM),0)/1000,2) end AS Turning_Ingot,case when (round(isnull((select isnull(sum(IngotsLM),0) from #TempIngotsLM),0)/1000,2)) <0 then 0 else round(isnull((select isnull(sum(IngotsLM),0) from #TempIngotsLM),0)/1000,2) end AS Ingots,sum(isnull(SAND,0)) AS SAND,case when (round(isnull((select isnull(sum(Runner_RiserLM),0) from #TempRunner_RiserLM),0)/1000,2))<0 then 0 else round(isnull((select isnull(sum(Runner_RiserLM),0) from #TempRunner_RiserLM),0)/1000,2) end AS Runner_Riser,sum(isnull((ProdPlan+Std_Stock)-(Turning_Ingot+Ingots+Runner_Riser+SAND) ,0)) as Total,round(sum(isnull((ProdPlan+Std_Stock)-(Turning_Ingot+Ingots+Runner_Riser+SAND) ,0)*LossPer/100),2) as With_Melting_Loss ,sum(isnull(isnull((ProdPlan+Std_Stock)-(Turning_Ingot+Ingots+Runner_Riser+SAND) ,0)+ round(isnull((ProdPlan+Std_Stock)-(Turning_Ingot+Ingots+Runner_Riser+SAND) ,0)*LossPer/100,2),0)) as Material_Purchase,LossPer from #Temp4 group by PROD_RAW_TYPE,LossPer drop table #Temp drop table #Temp2 drop table #Temp3 drop table #Temp4 drop table #TempSRSM_AC4B drop table #TempSRSM_